import React, { useEffect, useState } from 'react';
import Chart from "react-apexcharts";

const Charts = () => {

  const series = [ //data on the y-axis
    {
      name: "Ответ 1",
      data: [10,30,40]
    },
    {
      name: "Ответ 2",
      data: [50,20,40]
    },
    {
      name: "Ответ 3",
      data: [40,50,20]
    },

  ];

  const options = {
    chart: {
      type: 'bar',
      height: 350,
      stacked: true,
      stackType: '100%'
    },
    plotOptions: {
      bar: {
        horizontal: true,
      },
    },
    title: {
      text: 'Статистика'
    },
    xaxis: {
      categories: [2008, 2009, 2010],
    },
    legend: {
      position: 'top',
      horizontalAlign: 'left',
      offsetX: 40
    }
  }
  

  return (
    <div>
      <Chart
        options={options}
        series={series}
        type="bar"
        width="450"
      />
    </div>
  );

}

export default Charts;
