import React, { useEffect, useState } from 'react';
import Chart from "react-apexcharts";

const Chart2 = () => {
    const [test, setTest] = useState([0.1, 0.4, 0.2, 0.8, 0.5, 0.5, 0.9]);
    const { data, refetch} = useQuery();

    const handleClick = () => {
        console.log("123");
        let count = 0.5;
        setTest(oldArray => [...oldArray, count]);
        console.log(test);
        refetch();
    }

    const config = {
        series: [
            {
                name: "Подпись",
                data: test
            }
        ],
        options: {
            chart: {
                height: '350px',
                width: '500px',
                type: 'line',
                zoom: {
                    enabled: false
                }
            },
            toolbar: {
                enabled: false
            },
            grid: {
                row: {
                    colors: ['#f3f3f3', 'transparent'], 
                    opacity: 0.5
                },
            },
            dataLabels: {
                enabled: false,
            },
            stroke: {
                curve: 'straight'
            },
            title: {
                text: 'Название графика',
                align: 'left'
            },
            xaxis: {
                categories: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul']
            },
            yaxis: {
                title: {
                    text: 'Подпись оси Y'
                },
                min: 0,
                max: 1
            }
        },
    }

    return (
        <div>
            <button onClick= {handleClick} >test</button>
            <Chart options={config.options} type="area" height={300} width={500} series={config.series} />
        </div>
    );

}


export default Chart2;
