import React, { useEffect, useState } from 'react';
import Chart from "react-apexcharts";

const Chart2 = () => {

    const [values, setValues] = useState([[]]);
    const [names, setNames] = useState([]);

    useEffect(() => {
        const getData = async () => {
            const url = 'http://localhost:8000/test';
            try {
                const response = await fetch(url);
                const data = await response.json();
                setValues(data.map((item) => item.values));
                setNames(data.map((item) => item.name));
            } catch (error) {
                console.log(error);
            }
        };
        getData();
    }, []);


    const config ={
        series: [
            {
              name: "High - 2013",
              data: [0.1, 0.4, 0.2, 0.8, 0.5, 0.5, 0.9]
            }
          ],
          options: {
            chart: {
              height: 350,
              type: 'line',
              dropShadow: {
                enabled: true,
                color: '#000',
                top: 18,
                left: 7,
                blur: 10,
                opacity: 0.2
              },
              toolbar: {
                show: false
              }
            },
            colors: ['#77B6EA', '#545454'],
            dataLabels: {
              enabled: true,
            },
            stroke: {
              curve: 'smooth'
            },
            title: {
              text: 'Average High & Low Temperature',
              align: 'left'
            },
            grid: {
              borderColor: '#e7e7e7',
              row: {
                colors: ['#f3f3f3', 'transparent'], // takes an array which will be repeated on columns
                opacity: 0.5
              },
            },
            markers: {
              size: 1
            },
            xaxis: {
              categories: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul'],
              title: {
                text: 'Month'
              }
            },
            yaxis: {
              title: {
                text: 'Temperature'
              },
              min: 0,
              max: 1
            },
            legend: {
              position: 'top',
              horizontalAlign: 'right',
              floating: true,
              offsetY: -25,
              offsetX: -5
            }
          },
        
        
        }
    
    return (
        <div>
            <Chart options={config.options} type="area" weight="50%" height="300px" series={config.series} />
        </div>
    );

}


export default Chart2;
