import React, { useEffect, useState } from 'react';
import Chart from "react-apexcharts";

const Charts = () => {

  const [values, setValues] = useState([[]]);
  const [names, setNames] = useState();

  useEffect(() => {
    const getData = async () => {
      const url = 'http://localhost:8000/temperature';
      try {
        const response = await fetch(url);
        const data = await response.json();
        //console.log(data);
        setValues(data.map((item) => item.values));
        console.log(values[0]);
        setNames(data.map((item) => item.name));
      } catch (error) {
        console.log(error);
      }
    };
    getData();
  }, []);

  const series = [
    {
      name: "Marine Sprite",
      data: values[0]
    },
    {
      name: "Striking Calf",
      data: [50, 20, 40, 15, 10]
    },
    {
      name: "Tank Picture",
      data: [10, 20, 5, 15, 40]
    },
    {
      name: "Bucket Slope",
      data: [10, 15, 5, 5, 20]
    },
    {
      name: "Reborn Kid",
      data: [20, 15, 10, 50, 20]
    },

  ];

  const categories = [12, 13, 14];

  const options = {
    chart: {
      type: 'bar',
      height: 150,
      stacked: true,
      stackType: '100%'
    },
    plotOptions: {
      bar: {
        horizontal: true,
      },
    },
    title: {
      text: 'Статистика'
    },
    xaxis: {
      categories: categories,
    },
    legend: {
      position: 'top',
      horizontalAlign: 'left',
      offsetX: 40
    }
  }


  return (
    <div>
      <Chart
        options={options}
        series={series}
        type="bar"
        width="450"
      />
    </div>
  );

}


export default Charts;
