import React, { useEffect, useState } from 'react';
import Chart from "react-apexcharts";

const Chart2 = () => {

  const [values, setValues] = useState([[]]);
  const [names, setNames] = useState([]);

  useEffect(() => {
    const getData = async () => {
      const url = 'http://localhost:8000/test';
      try {
        const response = await fetch(url);
        const data = await response.json();
        setValues(data.map((item) => item.values));
        setNames(data.map((item) => item.name));
      } catch (error) {
        console.log(error);
      }
    };
    getData();
  }, []);

  
  const series = [
    {
      name: "Temperature in Fahrenheit", //will be displayed on the y-axis
      data: [43, 53, 50, 57]
    }
  ];
  const options = {
    chart: {
      id: "simple-bar"
    },
    xaxis: {
      categories: [1, 2, 3, 4] //will be displayed on the x-asis
    }
  };
  return (
    <div>
      <Chart options={options} type="bar" series={series} width="80%" />
    </div>
  );

}


export default Chart2;
