import React, { useEffect, useState } from 'react';
import Chart from "react-apexcharts";

const Charts = () => {

  const [avarageTemp, setAvarageTemp] = useState([]);

  useEffect(() => {
    const getData = async () => {
    const url = 'http://localhost:8000/temperature';
    try {
      const response = await fetch(url);
      const data = await response.json();
      console.log(data);
      setAvarageTemp(data?.map((item) => item.data));
      setDate(data?.map((item) => item.date));
    } catch (error) {
        console.log(error);
    }
  };
    getData();
  }, []);

  const series = [ //data on the y-axis
    {
      name: "Ответ 1",
      data: avarageTemp
    },
    {
      name: "Ответ 2",
      data: [50,20,40]
    },
    {
      name: "Ответ 3",
      data: [40,50,20]
    },

  ];

  const options = {
    chart: {
      type: 'bar',
      height: 350,
      stacked: true,
      stackType: '100%'
    },
    plotOptions: {
      bar: {
        horizontal: true,
      },
    },
    title: {
      text: 'Статистика'
    },
    xaxis: {
      categories: [2008, 2009, 2010],
    },
    legend: {
      position: 'top',
      horizontalAlign: 'left',
      offsetX: 40
    }
  }
  

  return (
    <div>
      <Chart
        options={options}
        series={series}
        type="bar"
        width="450"
      />
    </div>
  );

}

export default Charts;
