import React, { useEffect, useState } from 'react';
import Chart from "react-apexcharts";

const Charts = () => {

  const [values, setValues] = useState([[]]);
  const [names, setNames] = useState([]);

  useEffect(() => {
    const getData = async () => {
      const url = 'http://localhost:8000/temperature';
      try {
        const response = await fetch(url);
        const data = await response.json();
        //console.log(data);
        setValues(data.map((item) => item.values));
        setNames(data.map((item) => item.name));
      } catch (error) {
        console.log(error);
      }
    };
    getData();
  }, []);

  var obj = {};
  const series = [];

  for(let i = 0; i < values.length; i++){
    obj["name"] = names[0];
    obj["data"] = values[0];
series.push(obj);
  }  

  

  const categories = [1, 2, 3, 4];

  const options = {
    chart: {
      type: 'bar',
      stacked: true,
      stackType: '100%'
    },
    plotOptions: {
      bar: {
        horizontal: true,
      },
    },
    title: {
      text: 'Статистика'
    },
    xaxis: {
      categories: categories,
    },
    legend: {
      position: 'top',
      horizontalAlign: 'left',
      offsetX: 40
    }
  }


  return (
    <div>
      <Chart
        options={options}
        series={series}
        type="bar"
        height = "200"
        width="850"
      />
    </div>
  );

}


export default Charts;
